CREATE TABLE IF NOT EXISTS Genres
(
    genre_id int generated by default as identity primary key,
    name     varchar(30) UNIQUE
);


CREATE TABLE IF NOT EXISTS Films
(
    film_id      int generated by default as identity primary key,
    name         varchar(100),
    description  varchar(200),
    release_date date,
    duration     int,
    mpa          varchar(30)
);

create unique index IF NOT EXISTS Films_index
    on Films (film_id);

CREATE TABLE IF NOT EXISTS Films_genre
(
    film_id  int REFERENCES Films (film_id),
    genre_id int REFERENCES Genres (genre_id),
    primary key (film_id, genre_id)
);

CREATE TABLE IF NOT EXISTS Users
(
    user_id  int generated by default as identity primary key,
    login    varchar(100),
    name     varchar(100),
    email    varchar(200) UNIQUE,
    birthday date
);

create index IF NOT EXISTS Users_index
    on Users (user_id);

CREATE TABLE IF NOT EXISTS Friendships
(
    user_id      int REFERENCES Users (user_id),
    friend_id    int REFERENCES Users (user_id),
    is_confirmed boolean,
    primary key (user_id, friend_id)
);


CREATE TABLE IF NOT EXISTS Likes
(
    film_id int REFERENCES Films (film_id),
    user_id int REFERENCES Users (user_id),
    primary key (film_id, user_id)
);

create index IF NOT EXISTS Likes_index
    on Likes (film_id);

CREATE TABLE IF NOT EXISTS Reviews
(
    review_id   INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    content     TEXT,
    is_positive BOOLEAN NOT NULL,
    user_id     LONG    NOT NULL REFERENCES Users (user_id),
    film_id     LONG    NOT NULL REFERENCES Films (film_id),
    useful      INTEGER
);

CREATE TABLE IF NOT EXISTS Reviews_reaction
(
    review_id INTEGER REFERENCES Reviews (review_id),
    user_id   INTEGER REFERENCES Users (user_id),
    reaction  BOOLEAN
);
